# Function to get team colors automatically
get_cfb_colors <- function(team_name, type = "primary") {
  if (team_name %in% names(CFB_TEAM_COLORS)) {
    return(CFB_TEAM_COLORS[[team_name]][[type]])
  }
  # Default fallback colors
  return(if(type == "primary") "#003366" else "#999999")
}
